$(".likes").each((function(){$(this).on("click",(function(e){e.preventDefault();let t=$(this).find(" .likes-value");console.log(t.text()),$.ajax({type:"POST",url:t.attr("href"),success:function(e){likesCountvalue=parseInt(t.attr("data-likes")),1==e.data.deletelike?likesCountvalue-=1:likesCountvalue+=1,t.attr("data-likes",likesCountvalue),t.html(`${likesCountvalue} Likes`)},error:function(e){console.log("error in completing the request",e.responseText)}})}))}));